# Modify the cyk_parse function to return the complete chart and the parse tree
def cyk_parse(sentence, grammar):
    words = sentence.split()
    n = len(words)
    
    # Initialize the chart with empty sets
    chart = [[set() for _ in range(n - i)] for i in range(n)]

    # Fill in the diagonal of the chart with the possible non-terminals for each word
    for i in range(n):
        word = words[i]
        chart[0][i] = set(rule.lhs() for rule in grammar.productions(rhs=word))

    # CYK algorithm
    for length in range(2, n + 1):
        for start in range(n - length + 1):
            end = start + length
            for mid in range(start + 1, end):
                # Check all possible combinations of non-terminals
                for rule in grammar.productions():
                    if len(rule.rhs()) == 2:
                        left, right = rule.rhs()
                        left_set = chart[mid - start - 1][start]
                        right_set = chart[end - mid - 1][mid]

                        # Ensure that the chart cell is initialized with an empty set
                        if not chart[length - 1][start]:
                            chart[length - 1][start] = set()

                        if left in left_set and right in right_set:
                            chart[length - 1][start].add(rule.lhs())

    # Return the complete chart
    return chart

def build_parse_tree(chart, sentence, grammar, symbol, start, length):
    if length == 1:
        # Leaf node
        return Tree(symbol, [sentence[start]])

    tree = None

    for rule in grammar.productions(lhs=symbol):
        if len(rule.rhs()) == length:
            left, right = rule.rhs()

            for mid in range(start + 1, start + length):
                left_set = chart[mid - start - 1][start]
                right_set = chart[start + length - mid - 1][mid]

                if left in left_set and right in right_set:
                    left_tree = build_parse_tree(chart, sentence, grammar, left, start, mid - start)[0]
                    right_tree = build_parse_tree(chart, sentence, grammar, right, mid, start + length - mid)[0]

                    tree = Tree(symbol, [left_tree, right_tree])

    return [tree] if tree else []

def display_tree(tree, graph=None, parent_node=None, level=0):
    if graph is None:
        graph = nx.DiGraph()

    if isinstance(tree, Tree):
        current_node = f"{tree.label()}-{level}"  # pastikan tree.label() memberikan string yang diinginkan
        graph.add_node(current_node, label=str(tree.label()))

        if parent_node is not None:
            graph.add_edge(parent_node, current_node)

        for i, subtree in enumerate(tree):
            display_tree(subtree, graph, current_node, level + i)
    else:
        current_node = f"{tree}-{level}"
        graph.add_node(current_node, label=str(tree))
        if parent_node is not None:
            graph.add_edge(parent_node, current_node)

    return graph

def display_sentence_structure(tree, level=0):
    if isinstance(tree, Tree):
        result = "(" + str(tree.label()) + " "
        for subtree in tree:
            result += display_sentence_structure(subtree, level + 1)
        result += ")"
    else:
        result = str(tree) + " "

    return result

def display_result(chart, sentence, grammar):
    # Check if parsing is successful
    if chart[-1][0]:
        st.success("Parsing kalimat '{}' Berhasil".format(sentence))
        
        # Choose the start symbol, for example, 'S'
        start_symbol = nltk.grammar.Nonterminal('S')

        # Build parse tree for the chosen start symbol
        parse_trees = build_parse_tree(chart, sentence, grammar, start_symbol, 0, len(sentence))

        # Display the first parse tree using networkx and matplotlib
        if parse_trees:
            parse_tree = parse_trees[0]
            st.write(f"Parse tree for start symbol '{start_symbol}':")
            graph = display_tree(parse_tree)
            pos = nx.spring_layout(graph)
            labels = nx.get_node_attributes(graph, 'label')

            plt.figure(figsize=(8, 6))
            nx.draw(graph, pos, labels=labels, with_labels=True, node_size=5000, font_weight='bold', font_size=10, node_color='skyblue')
            plt.title(f'Parse Tree for {start_symbol}')
            st.pyplot(plt)
            
            # Display sentence structure for the first parse tree
            sentence_structure = display_sentence_structure(parse_tree)
            st.write(f"Sentence Structure: {sentence_structure}")
        else:
            st.warning("No parse tree found.")
    else:
        # Display an error message if parsing failed
        st.error("Parsing failed")

def construct_bracketed_string(chart, words):
    n = len(words)
    non_terminals = chart[n - 1][0]

    if 'K' not in non_terminals:
        return "Error construct_bracketed_string"

    return recursive_construct(chart, words, 'K', 0, n)

def recursive_construct(chart, words, non_terminal, start, end):
    if end - start == 1:
        return f"({non_terminal} {words[start]})"
    
    for length in range(1, end - start):
        left_set = chart[length - 1][start]
        right_set = chart[end - start - length - 1][start + length]

        for rule in left_set:
            if rule.rhs() == () and rule.lhs() == non_terminal:
                return f"({non_terminal} {words[start]})"
            # Check if the right-hand side of the rule is a list
            if len(rule.rhs()) == 2 and rule.rhs()[0] == non_terminal and rule.rhs()[1] in right_set:
                left = recursive_construct(chart, words, rule.rhs()[0], start, start + length)
                right = recursive_construct(chart, words, rule.rhs()[1], start + length, end)
                return f"({non_terminal} {left} {right})"
    
    return f"({non_terminal} {words[start]})"

